import { useState } from "react";
import type { ReactNode } from "react";
import Uppy from "@uppy/core";
import { DashboardModal } from "@uppy/react";
// Uppy CSS imports temporarily disabled due to Vite resolution issues
// import "@uppy/core/dist/style.css";
// import "@uppy/dashboard/dist/style.css";
import AwsS3 from "@uppy/aws-s3";
import type { UploadResult } from "@uppy/core";
import { Button } from "@/components/ui/button";

interface ObjectUploaderProps {
  maxNumberOfFiles?: number;
  maxFileSize?: number;
  onGetUploadParameters: () => Promise<{
    method: "PUT";
    url: string;
  }>;
  onComplete?: (
    result: UploadResult<Record<string, unknown>, Record<string, unknown>>
  ) => void;
  buttonClassName?: string;
  children: ReactNode;
}

/**
 * A file upload component that renders as a button and provides a modal interface for
 * file management.
 * 
 * Features:
 * - Renders as a customizable button that opens a file upload modal
 * - Provides a modal interface for:
 *   - File selection
 *   - File preview
 *   - Upload progress tracking
 *   - Upload status display
 * 
 * The component uses Uppy under the hood to handle all file upload functionality.
 * All file management features are automatically handled by the Uppy dashboard modal.
 * 
 * @param props - Component props
 * @param props.maxNumberOfFiles - Maximum number of files allowed to be uploaded
 *   (default: 1)
 * @param props.maxFileSize - Maximum file size in bytes (default: 10MB)
 * @param props.onGetUploadParameters - Function to get upload parameters (method and URL).
 *   Typically used to fetch a presigned URL from the backend server for direct-to-S3
 *   uploads.
 * @param props.onComplete - Callback function called when upload is complete. Typically
 *   used to make post-upload API calls to update server state and set object ACL
 *   policies.
 * @param props.buttonClassName - Optional CSS class name for the button
 * @param props.children - Content to be rendered inside the button
 */
export function ObjectUploader({
  maxNumberOfFiles = 1,
  maxFileSize = 10485760, // 10MB default
  onGetUploadParameters,
  onComplete,
  buttonClassName,
  children,
}: ObjectUploaderProps) {
  const [showModal, setShowModal] = useState(false);
  const [uppy] = useState(() =>
    new Uppy({
      restrictions: {
        maxNumberOfFiles,
        maxFileSize,
      },
      autoProceed: false,
    })
      .use(AwsS3, {
        shouldUseMultipart: false,
        getUploadParameters: onGetUploadParameters,
      })
      .on("complete", (result) => {
        onComplete?.(result);
      })
  );

  return (
    <div>
      <Button onClick={() => setShowModal(true)} className={buttonClassName}>
        {children}
      </Button>

      <DashboardModal
        uppy={uppy}
        open={showModal}
        onRequestClose={() => setShowModal(false)}
        proudlyDisplayPoweredByUppy={false}
      />
    </div>
  );
}

client/src/components/ai-chat.tsx.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { 
  Bot, 
  User, 
  Send, 
  ArrowLeft, 
  Loader2,
  Copy,
  ThumbsUp,
  ThumbsDown,
  Sparkles
} from "lucide-react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp: Date;
}

interface AiChatProps {
  initialMessage?: string;
  onBack?: () => void;
}

export function AiChat({ initialMessage, onBack }: AiChatProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState(initialMessage || "");
  const [sessionId] = useState(() => `session_${Date.now()}`);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  // Load conversation history
  const { data: conversationHistory } = useQuery<any[]>({
    queryKey: ["/api/ai/conversations", sessionId],
    enabled: !initialMessage, // Don't load history if we have an initial message
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/ai/chat", {
        message,
        sessionId,
        context: getContext(),
      });
      return response.json();
    },
    onSuccess: (data, variables) => {
      // Add assistant response
      setMessages(prev => [
        ...prev,
        {
          id: `assistant_${Date.now()}`,
          role: "assistant",
          content: data.response,
          timestamp: new Date(),
        }
      ]);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to get AI response. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Initialize with welcome message and initial message if provided
  useEffect(() => {
    if (messages.length === 0) {
      const welcomeMessage: Message = {
        id: "welcome",
        role: "assistant",
        content: "Hello! I'm your AI recruitment assistant. I can help you with candidate analysis, job matching, interview questions, and recruitment insights. How can I assist you today?",
        timestamp: new Date(),
      };

      if (initialMessage) {
        setMessages([welcomeMessage]);
        // Auto-send the initial message
        handleSendMessage(initialMessage);
      } else {
        setMessages([welcomeMessage]);
      }
    }
  }, [initialMessage]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Load conversation history
  useEffect(() => {
    if (conversationHistory && conversationHistory.length > 0 && !initialMessage) {
      const historyMessages: Message[] = conversationHistory.flatMap((conv: any) => [
        {
          id: `user_${conv.id}`,
          role: "user" as const,
          content: conv.message,
          timestamp: new Date(conv.createdAt),
        },
        {
          id: `assistant_${conv.id}`,
          role: "assistant" as const,
          content: conv.response,
          timestamp: new Date(conv.createdAt),
        },
      ]);
      setMessages(prev => prev.length === 1 ? [...prev, ...historyMessages] : prev);
    }
  }, [conversationHistory, initialMessage]);

  const getContext = () => {
    // Provide context about the current recruitment state
    return "User is an HR manager working with the AI recruitment system. They may ask about candidates, jobs, interviews, or general recruitment advice.";
  };

  const handleSendMessage = (messageText: string = inputValue) => {
    if (!messageText.trim() || sendMessageMutation.isPending) return;

    // Add user message
    const userMessage: Message = {
      id: `user_${Date.now()}`,
      role: "user",
      content: messageText,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue("");

    // Send to AI
    sendMessageMutation.mutate(messageText);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Message copied to clipboard",
    });
  };

  const suggestedPrompts = [
    "Analyze the top candidates for our Senior Developer role",
    "Generate interview questions for a Product Manager position",
    "What are the best practices for remote interviews?",
    "How can I improve our recruitment conversion rates?",
    "Help me write a compelling job description",
    "What should I look for in a candidate's resume?",
  ];

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="bg-card border-b border-border p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            {onBack && (
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={onBack}
                data-testid="button-back"
              >
                <ArrowLeft className="w-4 h-4" />
              </Button>
            )}
            <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
              <Bot className="text-primary-foreground w-4 h-4" />
            </div>
            <div>
              <h2 className="font-semibold text-foreground">AI Assistant</h2>
              <p className="text-xs text-muted-foreground">
                {sendMessageMutation.isPending ? "Thinking..." : "Online"}
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-1">
            <Badge variant="secondary" className="text-xs">
              <Sparkles className="w-3 h-3 mr-1" />
              GPT-5
            </Badge>
          </div>
        </div>
      </div>

      {/* Messages */}
      <ScrollArea className="flex-1 p-4">
        <div className="space-y-4 max-w-4xl mx-auto">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex items-start space-x-3 ${
                message.role === "user" ? "justify-end" : "justify-start"
              }`}
            >
              {message.role === "assistant" && (
                <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center flex-shrink-0">
                  <Bot className="text-primary-foreground w-4 h-4" />
                </div>
              )}

              <div
                className={`max-w-[70%] p-4 rounded-lg ${
                  message.role === "user"
                    ? "bg-primary text-primary-foreground"
                    : "bg-accent text-accent-foreground"
                }`}
                data-testid={`message-${message.role}-${message.id}`}
              >
                <div className="prose prose-sm max-w-none">
                  <p className="whitespace-pre-wrap">{message.content}</p>
                </div>

                <div className="flex items-center justify-between mt-2 text-xs opacity-70">
                  <span>
                    {message.timestamp.toLocaleTimeString([], { 
                      hour: "2-digit", 
                      minute: "2-digit" 
                    })}
                  </span>

                  {message.role === "assistant" && (
                    <div className="flex items-center space-x-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-6 px-2"
                        onClick={() => copyToClipboard(message.content)}
                      >
                        <Copy className="w-3 h-3" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-6 px-2"
                      >
                        <ThumbsUp className="w-3 h-3" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        className="h-6 px-2"
                      >
                        <ThumbsDown className="w-3 h-3" />
                      </Button>
                    </div>
                  )}
                </div>
              </div>

              {message.role === "user" && (
                <div className="w-8 h-8 bg-secondary rounded-lg flex items-center justify-center flex-shrink-0">
                  <User className="text-secondary-foreground w-4 h-4" />
                </div>
              )}
            </div>
          ))}

          {sendMessageMutation.isPending && (
            <div className="flex items-start space-x-3">
              <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center flex-shrink-0">
                <Bot className="text-primary-foreground w-4 h-4" />
              </div>
              <div className="bg-accent text-accent-foreground p-4 rounded-lg">
                <div className="flex items-center space-x-2">
                  <Loader2 className="w-4 h-4 animate-spin" />
                  <span className="text-sm">AI is thinking...</span>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>
      </ScrollArea>

      {/* Suggested Prompts */}
      {messages.length === 1 && !sendMessageMutation.isPending && (
        <div className="p-4 border-t border-border">
          <p className="text-sm text-muted-foreground mb-3">Try asking about:</p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
            {suggestedPrompts.slice(0, 4).map((prompt, index) => (
              <Button
                key={index}
                variant="outline"
                size="sm"
                className="text-left justify-start text-xs h-auto py-2 px-3"
                onClick={() => handleSendMessage(prompt)}
                data-testid={`button-prompt-${index}`}
              >
                {prompt}
              </Button>
            ))}
          </div>
        </div>
      )}

      {/* Input */}
      <div className="p-4 border-t border-border">
        <div className="flex space-x-2">
          <Input
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask me anything about recruitment..."
            disabled={sendMessageMutation.isPending}
            className="flex-1"
            data-testid="input-message"
          />
          <Button
            onClick={() => handleSendMessage()}
            disabled={!inputValue.trim() || sendMessageMutation.isPending}
            data-testid="button-send"
          >
            {sendMessageMutation.isPending ? (
              <Loader2 className="w-4 h-4 animate-spin" />
            ) : (
              <Send className="w-4 h-4" />
            )}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/candidate-card.tsx.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { 
  Mail, 
  Phone, 
  MapPin, 
  Calendar, 
  DollarSign,
  FileText,
  Eye,
  MessageSquare,
  Upload,
  Brain,
  Clock,
  Tag
} from "lucide-react";
import { type Candidate } from "@shared/schema";

interface CandidateCardProps {
  candidate: Candidate;
  onUploadResume?: () => void;
}

export function CandidateCard({ candidate, onUploadResume }: CandidateCardProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case "applied":
        return "bg-blue-500/10 text-blue-700 border-blue-500/20";
      case "screening":
        return "bg-yellow-500/10 text-yellow-700 border-yellow-500/20";
      case "interview":
        return "bg-purple-500/10 text-purple-700 border-purple-500/20";
      case "offer":
        return "bg-green-500/10 text-green-700 border-green-500/20";
      case "hired":
        return "bg-emerald-500/10 text-emerald-700 border-emerald-500/20";
      case "rejected":
        return "bg-red-500/10 text-red-700 border-red-500/20";
      default:
        return "bg-gray-500/10 text-gray-700 border-gray-500/20";
    }
  };

  const getSourceColor = (source: string) => {
    switch (source) {
      case "linkedin":
        return "bg-blue-500/10 text-blue-700";
      case "job_board":
        return "bg-green-500/10 text-green-700";
      case "referral":
        return "bg-purple-500/10 text-purple-700";
      default:
        return "bg-gray-500/10 text-gray-700";
    }
  };

  const formatSalary = (amount?: number) => {
    if (!amount) return "Not specified";
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatLastContacted = (date?: Date) => {
    if (!date) return "Never";
    const now = new Date();
    const diffInDays = Math.floor((now.getTime() - new Date(date).getTime()) / (1000 * 60 * 60 * 24));

    if (diffInDays === 0) return "Today";
    if (diffInDays === 1) return "Yesterday";
    if (diffInDays < 7) return `${diffInDays} days ago`;
    if (diffInDays < 30) return `${Math.floor(diffInDays / 7)} weeks ago`;
    return `${Math.floor(diffInDays / 30)} months ago`;
  };

  const skills = candidate.skills as string[] || [];
  const tags = candidate.tags as string[] || [];
  const matchScore = Number(candidate.matchScore) || 0;

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-3">
            <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
              <span className="text-lg font-semibold text-primary">
                {candidate.name?.charAt(0) || 'C'}
              </span>
            </div>
            <div>
              <h3 className="font-semibold text-foreground" data-testid={`text-name-${candidate.id}`}>
                {candidate.name || 'Unknown Name'}
              </h3>
              <p className="text-sm text-muted-foreground" data-testid={`text-position-${candidate.id}`}>
                {candidate.position || 'Position not specified'}
              </p>
            </div>
          </div>
          <div className="flex flex-col items-end space-y-1">
            <Badge 
              className={getStatusColor(candidate.status)}
              data-testid={`badge-status-${candidate.id}`}
            >
              {candidate.status}
            </Badge>
            {candidate.source && (
              <Badge 
                variant="outline" 
                className={`text-xs ${getSourceColor(candidate.source)}`}
                data-testid={`badge-source-${candidate.id}`}
              >
                {candidate.source}
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Contact Information */}
        <div className="space-y-2">
          {candidate.email && (
            <div className="flex items-center text-sm text-muted-foreground">
              <Mail className="w-4 h-4 mr-2" />
              <span data-testid={`text-email-${candidate.id}`}>{candidate.email}</span>
            </div>
          )}
          {candidate.phone && (
            <div className="flex items-center text-sm text-muted-foreground">
              <Phone className="w-4 h-4 mr-2" />
              <span data-testid={`text-phone-${candidate.id}`}>{candidate.phone}</span>
            </div>
          )}
          {candidate.location && (
            <div className="flex items-center text-sm text-muted-foreground">
              <MapPin className="w-4 h-4 mr-2" />
              <span data-testid={`text-location-${candidate.id}`}>{candidate.location}</span>
            </div>
          )}
        </div>

        {/* Experience and Salary */}
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <p className="text-muted-foreground">Experience</p>
            <p className="font-medium" data-testid={`text-experience-${candidate.id}`}>
              {candidate.experience ? `${candidate.experience} years` : 'Not specified'}
            </p>
          </div>
          <div>
            <p className="text-muted-foreground">Salary Expectation</p>
            <p className="font-medium" data-testid={`text-salary-${candidate.id}`}>
              {formatSalary(candidate.salaryExpectation ?? undefined)}
            </p>
          </div>
        </div>

        {/* Match Score */}
        {matchScore > 0 && (
          <div>
            <div className="flex items-center justify-between text-sm mb-2">
              <div className="flex items-center space-x-1">
                <Brain className="w-4 h-4 text-primary" />
                <span className="text-muted-foreground">AI Match Score</span>
              </div>
              <span className="font-medium text-chart-2" data-testid={`text-match-score-${candidate.id}`}>
                {matchScore}%
              </span>
            </div>
            <Progress value={matchScore} className="h-2" />
          </div>
        )}

        {/* Skills */}
        {skills.length > 0 && (
          <div>
            <p className="text-sm text-muted-foreground mb-2">Skills</p>
            <div className="flex flex-wrap gap-1">
              {skills.slice(0, 3).map((skill, index) => (
                <Badge 
                  key={index} 
                  variant="secondary" 
                  className="text-xs"
                  data-testid={`badge-skill-${candidate.id}-${index}`}
                >
                  {skill}
                </Badge>
              ))}
              {skills.length > 3 && (
                <Badge variant="secondary" className="text-xs">
                  +{skills.length - 3} more
                </Badge>
              )}
            </div>
          </div>
        )}

        {/* Tags */}
        {tags.length > 0 && (
          <div>
            <p className="text-sm text-muted-foreground mb-2">Tags</p>
            <div className="flex flex-wrap gap-1">
              {tags.map((tag, index) => (
                <Badge 
                  key={index} 
                  variant="outline" 
                  className="text-xs"
                  data-testid={`badge-tag-${candidate.id}-${index}`}
                >
                  <Tag className="w-3 h-3 mr-1" />
                  {tag}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* AI Summary */}
        {candidate.aiSummary && (
          <div className="p-3 bg-accent rounded-lg">
            <div className="flex items-center space-x-2 mb-1">
              <Brain className="w-4 h-4 text-primary" />
              <p className="text-xs text-muted-foreground">AI Summary</p>
            </div>
            <p className="text-sm text-accent-foreground line-clamp-2" data-testid={`text-ai-summary-${candidate.id}`}>
              {candidate.aiSummary}
            </p>
          </div>
        )}

        {/* Last Contacted */}
        <div className="flex items-center text-xs text-muted-foreground">
          <Clock className="w-3 h-3 mr-1" />
          <span>Last contacted: {formatLastContacted(candidate.lastContactedAt ?? undefined)}</span>
        </div>

        {/* Actions */}
        <div className="flex space-x-2 pt-2">
          <Button size="sm" className="flex-1" data-testid={`button-view-${candidate.id}`}>
            <Eye className="w-4 h-4 mr-2" />
            View Profile
          </Button>
          <Button size="sm" variant="outline" data-testid={`button-message-${candidate.id}`}>
            <MessageSquare className="w-4 h-4" />
          </Button>
          {candidate.resumeUrl ? (
            <Button size="sm" variant="outline" data-testid={`button-resume-${candidate.id}`}>
              <FileText className="w-4 h-4" />
            </Button>
          ) : (
            <Button 
              size="sm" 
              variant="outline" 
              onClick={onUploadResume}
              data-testid={`button-upload-resume-${candidate.id}`}
            >
              <Upload className="w-4 h-4" />
            </Button>
          )}
        </div>

        {/* Last Updated */}
        <div className="text-xs text-muted-foreground text-center pt-2 border-t">
          <Calendar className="w-3 h-3 mr-1 inline" />
          Updated {new Date(candidate.updatedAt!).toLocaleDateString()}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/interview-card.tsx.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Calendar, 
  Clock, 
  Video, 
  Phone, 
  MapPin,
  User,
  Building,
  Edit,
  CheckCircle,
  XCircle,
  AlertCircle
} from "lucide-react";
import { type Interview } from "@shared/schema";
import { useMutation, useQuery } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { apiRequest } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface InterviewCardProps {
  interview: Interview;
}

export function InterviewCard({ interview }: InterviewCardProps) {
  const { toast } = useToast();

  // Fetch candidate and job details
  const { data: candidate } = useQuery<any>({
    queryKey: ["/api/candidates", interview.candidateId],
    enabled: !!interview.candidateId,
  });

  const { data: job } = useQuery<any>({
    queryKey: ["/api/jobs", interview.jobId],
    enabled: !!interview.jobId,
  });

  const updateInterviewMutation = useMutation({
    mutationFn: async (data: Partial<Interview>) => {
      const response = await apiRequest("PUT", `/api/interviews/${interview.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/interviews"] });
      toast({
        title: "Interview updated",
        description: "Interview has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update interview.",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "scheduled":
        return "bg-blue-500/10 text-blue-700 border-blue-500/20";
      case "completed":
        return "bg-green-500/10 text-green-700 border-green-500/20";
      case "cancelled":
        return "bg-red-500/10 text-red-700 border-red-500/20";
      case "no-show":
        return "bg-gray-500/10 text-gray-700 border-gray-500/20";
      default:
        return "bg-gray-500/10 text-gray-700 border-gray-500/20";
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case "video":
        return Video;
      case "phone":
        return Phone;
      case "in-person":
        return MapPin;
      default:
        return Video;
    }
  };

  const formatDateTime = (date: Date) => {
    return new Intl.DateTimeFormat("en-US", {
      weekday: "short",
      month: "short",
      day: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    }).format(new Date(date));
  };

  const formatDuration = (minutes: number) => {
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return remainingMinutes > 0 ? `${hours}h ${remainingMinutes}m` : `${hours}h`;
  };

  const isUpcoming = new Date(interview.scheduledDate) > new Date();
  const isPast = new Date(interview.scheduledDate) < new Date();
  const TypeIcon = getTypeIcon(interview.type);

  const handleStatusUpdate = (status: string) => {
    updateInterviewMutation.mutate({ status });
  };

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-2">
              <TypeIcon className="w-4 h-4 text-muted-foreground" />
              <Badge 
                className={getStatusColor(interview.status)}
                data-testid={`badge-status-${interview.id}`}
              >
                {interview.status}
              </Badge>
              {interview.round > 1 && (
                <Badge variant="outline" className="text-xs">
                  Round {interview.round}
                </Badge>
              )}
            </div>

            <h3 className="font-semibold text-foreground" data-testid={`text-title-${interview.id}`}>
              {candidate?.name || 'Unknown Candidate'}
            </h3>
            <p className="text-sm text-muted-foreground" data-testid={`text-job-${interview.id}`}>
              {job?.title || 'Unknown Position'}
            </p>
          </div>

          {interview.rating && (
            <div className="text-right">
              <div className="flex items-center space-x-1">
                {[...Array(5)].map((_, i) => (
                  <div
                    key={i}
                    className={`w-3 h-3 rounded-full ${
                      i < interview.rating! ? "bg-yellow-400" : "bg-gray-200"
                    }`}
                  />
                ))}
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                {interview.rating}/5
              </p>
            </div>
          )}
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Date and Time */}
        <div className="space-y-2">
          <div className="flex items-center text-sm">
            <Calendar className="w-4 h-4 mr-2 text-muted-foreground" />
            <span data-testid={`text-datetime-${interview.id}`}>
              {formatDateTime(interview.scheduledDate)}
            </span>
          </div>
          <div className="flex items-center text-sm text-muted-foreground">
            <Clock className="w-4 h-4 mr-2" />
            <span data-testid={`text-duration-${interview.id}`}>
              {formatDuration(interview.duration)}
            </span>
          </div>
        </div>

        {/* Location/Link */}
        {interview.type === "video" && interview.meetingLink && (
          <div className="p-3 bg-accent rounded-lg">
            <p className="text-xs text-muted-foreground mb-1">Meeting Link</p>
            <a 
              href={interview.meetingLink}
              target="_blank"
              rel="noopener noreferrer"
              className="text-sm text-primary hover:underline"
              data-testid={`link-meeting-${interview.id}`}
            >
              Join Video Call
            </a>
          </div>
        )}

        {interview.type === "in-person" && interview.location && (
          <div className="flex items-center text-sm text-muted-foreground">
            <MapPin className="w-4 h-4 mr-2" />
            <span data-testid={`text-location-${interview.id}`}>{interview.location}</span>
          </div>
        )}

        {/* Interviewer */}
        {interview.interviewerId && (
          <div className="flex items-center text-sm text-muted-foreground">
            <User className="w-4 h-4 mr-2" />
            <span>Interviewer assigned</span>
          </div>
        )}

        {/* Feedback */}
        {interview.feedback && (
          <div className="p-3 bg-accent rounded-lg">
            <p className="text-xs text-muted-foreground mb-1">Feedback</p>
            <p className="text-sm text-accent-foreground line-clamp-3" data-testid={`text-feedback-${interview.id}`}>
              {interview.feedback}
            </p>
          </div>
        )}

        {/* Recommendation */}
        {interview.recommendation && (
          <div className="flex items-center space-x-2">
            {interview.recommendation === "hire" && (
              <CheckCircle className="w-4 h-4 text-green-600" />
            )}
            {interview.recommendation === "reject" && (
              <XCircle className="w-4 h-4 text-red-600" />
            )}
            {interview.recommendation === "next-round" && (
              <AlertCircle className="w-4 h-4 text-yellow-600" />
            )}
            <span className="text-sm capitalize" data-testid={`text-recommendation-${interview.id}`}>
              {interview.recommendation.replace("-", " ")}
            </span>
          </div>
        )}

        {/* Actions */}
        <div className="flex space-x-2 pt-2 border-t">
          {interview.status === "scheduled" && isUpcoming && (
            <>
              <Button 
                size="sm" 
                variant="outline"
                onClick={() => handleStatusUpdate("completed")}
                disabled={updateInterviewMutation.isPending}
                data-testid={`button-complete-${interview.id}`}
              >
                Mark Complete
              </Button>
              <Button 
                size="sm" 
                variant="outline"
                onClick={() => handleStatusUpdate("cancelled")}
                disabled={updateInterviewMutation.isPending}
                data-testid={`button-cancel-${interview.id}`}
              >
                Cancel
              </Button>
            </>
          )}

          {interview.status === "scheduled" && isPast && (
            <Button 
              size="sm" 
              variant="outline"
              onClick={() => handleStatusUpdate("no-show")}
              disabled={updateInterviewMutation.isPending}
              data-testid={`button-no-show-${interview.id}`}
            >
              Mark No Show
            </Button>
          )}

          <Button 
            size="sm" 
            className="flex-1"
            data-testid={`button-view-${interview.id}`}
          >
            <Edit className="w-4 h-4 mr-2" />
            View Details
          </Button>
        </div>

        {/* Time indicator */}
        <div className="text-xs text-center text-muted-foreground">
          {isUpcoming && interview.status === "scheduled" && (
            <span className="text-blue-600">
              Upcoming â€¢ {new Date(interview.scheduledDate) > new Date() ? 'In future' : 'Past due'}
            </span>
          )}
          {interview.status === "completed" && (
            <span className="text-green-600">Completed</span>
          )}
          {interview.status === "cancelled" && (
            <span className="text-red-600">Cancelled</span>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/job-card.tsx.content:
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  MapPin, 
  Calendar, 
  DollarSign,
  Users,
  Clock,
  Building,
  Edit,
  Eye,
  Pause,
  Play,
  X
} from "lucide-react";
import { type Job } from "@shared/schema";
import { useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { apiRequest } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";

interface JobCardProps {
  job: Job;
}

export function JobCard({ job }: JobCardProps) {
  const { toast } = useToast();

  const updateJobMutation = useMutation({
    mutationFn: async (data: Partial<Job>) => {
      const response = await apiRequest("PUT", `/api/jobs/${job.id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      toast({
        title: "Job updated",
        description: "Job status has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update job status.",
        variant: "destructive",
      });
    },
  });

  const deleteJobMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("DELETE", `/api/jobs/${job.id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      toast({
        title: "Job deleted",
        description: "Job has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete job.",
        variant: "destructive",
      });
    },
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active":
        return "bg-green-500/10 text-green-700 border-green-500/20";
      case "paused":
        return "bg-yellow-500/10 text-yellow-700 border-yellow-500/20";
      case "closed":
        return "bg-red-500/10 text-red-700 border-red-500/20";
      default:
        return "bg-gray-500/10 text-gray-700 border-gray-500/20";
    }
  };

  const formatSalary = (min?: number, max?: number) => {
    if (!min && !max) return "Not specified";
    const formatter = new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
      maximumFractionDigits: 0,
    });
    if (min && max) {
      return `${formatter.format(min)} - ${formatter.format(max)}`;
    }
    return formatter.format(min || max || 0);
  };

  const requirements = job.requirements as string[] || [];

  const handleStatusToggle = () => {
    const newStatus = job.status === "active" ? "paused" : "active";
    updateJobMutation.mutate({ status: newStatus });
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this job?")) {
      deleteJobMutation.mutate();
    }
  };

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="font-semibold text-foreground text-lg mb-1" data-testid={`text-title-${job.id}`}>
              {job.title}
            </h3>
            <div className="flex items-center space-x-4 text-sm text-muted-foreground">
              <div className="flex items-center">
                <Building className="w-4 h-4 mr-1" />
                <span data-testid={`text-department-${job.id}`}>{job.department}</span>
              </div>
              <div className="flex items-center">
                <MapPin className="w-4 h-4 mr-1" />
                <span data-testid={`text-location-${job.id}`}>{job.location}</span>
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <Badge 
              className={getStatusColor(job.status)}
              data-testid={`badge-status-${job.id}`}
            >
              {job.status}
            </Badge>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Job Details */}
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <p className="text-muted-foreground">Type</p>
            <p className="font-medium capitalize" data-testid={`text-type-${job.id}`}>
              {job.type.replace("-", " ")}
            </p>
          </div>
          <div>
            <p className="text-muted-foreground">Salary</p>
            <p className="font-medium" data-testid={`text-salary-${job.id}`}>
              {formatSalary(job.salaryMin ?? undefined, job.salaryMax ?? undefined)}
            </p>
          </div>
        </div>

        {/* Description */}
        <div>
          <p className="text-sm text-muted-foreground line-clamp-3" data-testid={`text-description-${job.id}`}>
            {job.description}
          </p>
        </div>

        {/* Requirements */}
        {requirements.length > 0 && (
          <div>
            <p className="text-sm text-muted-foreground mb-2">Requirements</p>
            <div className="flex flex-wrap gap-1">
              {requirements.slice(0, 3).map((req, index) => (
                <Badge 
                  key={index} 
                  variant="secondary" 
                  className="text-xs"
                  data-testid={`badge-requirement-${job.id}-${index}`}
                >
                  {req}
                </Badge>
              ))}
              {requirements.length > 3 && (
                <Badge variant="secondary" className="text-xs">
                  +{requirements.length - 3} more
                </Badge>
              )}
            </div>
          </div>
        )}

        {/* Stats */}
        <div className="flex items-center justify-between text-sm text-muted-foreground pt-2 border-t">
          <div className="flex items-center space-x-4">
            <div className="flex items-center">
              <Users className="w-4 h-4 mr-1" />
              <span>0 applicants</span>
            </div>
            <div className="flex items-center">
              <Calendar className="w-4 h-4 mr-1" />
              <span>Posted {new Date(job.createdAt!).toLocaleDateString()}</span>
            </div>
          </div>
        </div>

        {/* Actions */}
        <div className="flex space-x-2 pt-2">
          <Button size="sm" className="flex-1" data-testid={`button-view-${job.id}`}>
            <Eye className="w-4 h-4 mr-2" />
            View Details
          </Button>
          <Button 
            size="sm" 
            variant="outline"
            onClick={handleStatusToggle}
            disabled={updateJobMutation.isPending}
            data-testid={`button-toggle-status-${job.id}`}
          >
            {job.status === "active" ? (
              <Pause className="w-4 h-4" />
            ) : (
              <Play className="w-4 h-4" />
            )}
          </Button>
          <Button 
            size="sm" 
            variant="outline"
            data-testid={`button-edit-${job.id}`}
          >
            <Edit className="w-4 h-4" />
          </Button>
          <Button 
            size="sm" 
            variant="outline"
            onClick={handleDelete}
            disabled={deleteJobMutation.isPending}
            data-testid={`button-delete-${job.id}`}
          >
            <X className="w-4 h-4" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/prompt-template-ma.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient } from "@/lib/queryClient";
import { apiRequest } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Copy, 
  FileText,
  Brain,
  Users,
  MessageSquare,
  Filter,
  Search
} from "lucide-react";

interface PromptTemplate {
  id: string;
  name: string;
  description: string;
  category: "resume_analysis" | "job_matching" | "interview_questions" | "candidate_screening" | "general";
  template: string;
  variables: string[];
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

const categoryIcons = {
  resume_analysis: FileText,
  job_matching: Users,
  interview_questions: MessageSquare,
  candidate_screening: Filter,
  general: Brain,
};

const categoryLabels = {
  resume_analysis: "Resume Analysis",
  job_matching: "Job Matching",
  interview_questions: "Interview Questions",
  candidate_screening: "Candidate Screening",
  general: "General",
};

export function PromptTemplateManager() {
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<PromptTemplate | null>(null);
  const { toast } = useToast();

  const { data: templates, isLoading } = useQuery<PromptTemplate[]>({
    queryKey: ["/api/prompt-templates"],
  });

  const createTemplateMutation = useMutation({
    mutationFn: async (template: Omit<PromptTemplate, "id" | "createdAt" | "updatedAt">) => {
      const response = await apiRequest("POST", "/api/prompt-templates", template);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prompt-templates"] });
      setIsCreateDialogOpen(false);
      toast({
        title: "Template created",
        description: "Prompt template has been created successfully.",
      });
    },
  });

  const updateTemplateMutation = useMutation({
    mutationFn: async ({ id, ...template }: Partial<PromptTemplate> & { id: string }) => {
      const response = await apiRequest("PUT", `/api/prompt-templates/${id}`, template);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prompt-templates"] });
      setEditingTemplate(null);
      toast({
        title: "Template updated",
        description: "Prompt template has been updated successfully.",
      });
    },
  });

  const deleteTemplateMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/prompt-templates/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prompt-templates"] });
      toast({
        title: "Template deleted",
        description: "Prompt template has been deleted successfully.",
      });
    },
  });

  const filteredTemplates = templates?.filter(template => {
    const matchesCategory = selectedCategory === "all" || template.category === selectedCategory;
    const matchesSearch = template.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         template.description.toLowerCase().includes(searchQuery.toLowerCase());
    return matchesCategory && matchesSearch;
  }) || [];

  const handleDeleteTemplate = (id: string) => {
    if (window.confirm("Are you sure you want to delete this template?")) {
      deleteTemplateMutation.mutate(id);
    }
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "Template copied to clipboard",
    });
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center space-x-2">
                <Brain className="w-5 h-5" />
                <span>Prompt Templates</span>
              </CardTitle>
              <p className="text-sm text-muted-foreground mt-1">
                Manage AI prompt templates for consistent and effective interactions
              </p>
            </div>
            <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
              <DialogTrigger asChild>
                <Button data-testid="button-create-template">
                  <Plus className="w-4 h-4 mr-2" />
                  Create Template
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl">
                <DialogHeader>
                  <DialogTitle>Create New Prompt Template</DialogTitle>
                </DialogHeader>
                <TemplateForm 
                  onSubmit={(data) => createTemplateMutation.mutate(data)}
                  isLoading={createTemplateMutation.isPending}
                />
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
      </Card>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center space-x-4">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
              <Input
                placeholder="Search templates..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search-templates"
              />
            </div>

            <Select value={selectedCategory} onValueChange={setSelectedCategory}>
              <SelectTrigger className="w-48" data-testid="select-category-filter">
                <SelectValue placeholder="All Categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Categories</SelectItem>
                {Object.entries(categoryLabels).map(([value, label]) => (
                  <SelectItem key={value} value={value}>{label}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Templates Grid */}
      {isLoading ? (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </div>
      ) : (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {filteredTemplates.map((template) => {
            const IconComponent = categoryIcons[template.category];
            return (
              <Card key={template.id} className="hover:shadow-md transition-shadow">
                <CardHeader className="pb-3">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                        <IconComponent className="w-5 h-5 text-primary" />
                      </div>
                      <div>
                        <h3 className="font-semibold text-foreground" data-testid={`text-template-name-${template.id}`}>
                          {template.name}
                        </h3>
                        <p className="text-sm text-muted-foreground">
                          {template.description}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Badge 
                        variant={template.isActive ? "default" : "secondary"}
                        data-testid={`badge-status-${template.id}`}
                      >
                        {template.isActive ? "Active" : "Inactive"}
                      </Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="space-y-4">
                  <div>
                    <Badge variant="outline" className="text-xs">
                      {categoryLabels[template.category]}
                    </Badge>
                  </div>

                  {/* Variables */}
                  {template.variables.length > 0 && (
                    <div>
                      <p className="text-sm text-muted-foreground mb-2">Variables:</p>
                      <div className="flex flex-wrap gap-1">
                        {template.variables.map((variable, index) => (
                          <Badge key={index} variant="secondary" className="text-xs">
                            {`{{${variable}}}`}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Template Preview */}
                  <div className="p-3 bg-accent rounded-lg">
                    <p className="text-xs text-muted-foreground mb-1">Template Preview</p>
                    <p className="text-sm text-accent-foreground line-clamp-3" data-testid={`text-template-preview-${template.id}`}>
                      {template.template.substring(0, 200)}...
                    </p>
                  </div>

                  {/* Actions */}
                  <div className="flex space-x-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => copyToClipboard(template.template)}
                      data-testid={`button-copy-${template.id}`}
                    >
                      <Copy className="w-4 h-4 mr-2" />
                      Copy
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => setEditingTemplate(template)}
                      data-testid={`button-edit-${template.id}`}
                    >
                      <Edit className="w-4 h-4 mr-2" />
                      Edit
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleDeleteTemplate(template.id)}
                      disabled={deleteTemplateMutation.isPending}
                      data-testid={`button-delete-${template.id}`}
                    >
                      <Trash2 className="w-4 h-4 mr-2" />
                      Delete
                    </Button>
                  </div>

                  <div className="text-xs text-muted-foreground text-center pt-2 border-t">
                    Updated {new Date(template.updatedAt).toLocaleDateString()}
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}

      {filteredTemplates.length === 0 && !isLoading && (
        <Card className="h-64">
          <CardContent className="flex flex-col items-center justify-center h-full text-center">
            <Brain className="w-12 h-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium text-foreground mb-2">
              {searchQuery || selectedCategory !== "all" ? "No templates found" : "No templates yet"}
            </h3>
            <p className="text-sm text-muted-foreground mb-4">
              {searchQuery || selectedCategory !== "all" 
                ? "Try adjusting your search or filters" 
                : "Create your first prompt template to get started"
              }
            </p>
            {!searchQuery && selectedCategory === "all" && (
              <Button onClick={() => setIsCreateDialogOpen(true)}>
                <Plus className="w-4 h-4 mr-2" />
                Create First Template
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Template Dialog */}
      {editingTemplate && (
        <Dialog open={!!editingTemplate} onOpenChange={() => setEditingTemplate(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Edit Prompt Template</DialogTitle>
            </DialogHeader>
            <TemplateForm 
              template={editingTemplate}
              onSubmit={(data) => updateTemplateMutation.mutate({ ...data, id: editingTemplate.id })}
              isLoading={updateTemplateMutation.isPending}
            />
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

interface TemplateFormProps {
  template?: PromptTemplate;
  onSubmit: (data: any) => void;
  isLoading: boolean;
}

function TemplateForm({ template, onSubmit, isLoading }: TemplateFormProps) {
  const [formData, setFormData] = useState({
    name: template?.name || "",
    description: template?.description || "",
    category: template?.category || "general",
    template: template?.template || "",
    isActive: template?.isActive ?? true,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Extract variables from template
    const regex = /{{([^}]+)}}/g;
    const variables: string[] = [];
    let match;

    while ((match = regex.exec(formData.template)) !== null) {
      const variable = match[1].trim();
      if (!variables.includes(variable)) {
        variables.push(variable);
      }
    }

    onSubmit({
      ...formData,
      variables,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="text-sm font-medium">Name</label>
          <Input
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
            data-testid="input-template-name"
          />
        </div>
        <div>
          <label className="text-sm font-medium">Category</label>
          <Select value={formData.category} onValueChange={(value) => setFormData({ ...formData, category: value as any })}>
            <SelectTrigger data-testid="select-template-category">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {Object.entries(categoryLabels).map(([value, label]) => (
                <SelectItem key={value} value={value}>{label}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <label className="text-sm font-medium">Description</label>
        <Input
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          required
          data-testid="input-template-description"
        />
      </div>

      <div>
        <label className="text-sm font-medium">Template</label>
        <Textarea
          value={formData.template}
          onChange={(e) => setFormData({ ...formData, template: e.target.value })}
          rows={10}
          placeholder="Enter your prompt template. Use {{variableName}} for variables."
          required
          data-testid="textarea-template-content"
        />
        <p className="text-xs text-muted-foreground mt-1">
          Use double curly braces for variables: {`{{variableName}}`}
        </p>
      </div>

      <div className="flex items-center space-x-2">
        <input
          type="checkbox"
          id="isActive"
          checked={formData.isActive}
          onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
          data-testid="checkbox-template-active"
        />
        <label htmlFor="isActive" className="text-sm font-medium">
          Active
        </label>
      </div>

      <div className="flex justify-end space-x-2">
        <Button type="submit" disabled={isLoading} data-testid="button-save-template">
          {isLoading ? "Saving..." : "Save Template"}
        </Button>
      </div>
    </form>
  );
}

client/src/components/resume-uploader.ts.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { ObjectUploader } from "@/components/ObjectUploader";
import { ResumeAnalysis } from "@/components/resume-analysis";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { 
  Upload, 
  FileText, 
  CheckCircle, 
  AlertCircle, 
  Brain,
  Loader2
} from "lucide-react";
import type { ResumeAnalysisResult } from "@/types";

interface ResumeUploaderProps {
  candidateId: string;
  onAnalysisComplete?: (analysis: ResumeAnalysisResult) => void;
  onCandidateUpdate?: (candidate: any) => void;
}

export function ResumeUploader({ 
  candidateId, 
  onAnalysisComplete, 
  onCandidateUpdate 
}: ResumeUploaderProps) {
  const [uploadStatus, setUploadStatus] = useState<"idle" | "uploading" | "processing" | "complete" | "error">("idle");
  const [uploadProgress, setUploadProgress] = useState(0);
  const [analysisResult, setAnalysisResult] = useState<ResumeAnalysisResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const processResumeMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append("resume", file);

      const response = await fetch(`/api/candidates/${candidateId}/resume`, {
        method: "POST",
        body: formData,
        credentials: "include",
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || "Failed to process resume");
      }

      return response.json();
    },
    onSuccess: (data: ResumeAnalysisResult) => {
      setAnalysisResult(data);
      setUploadStatus("complete");
      onAnalysisComplete?.(data);
      onCandidateUpdate?.(data.candidate);

      toast({
        title: "Resume processed successfully",
        description: "AI analysis complete. Review the insights below.",
      });
    },
    onError: (error: Error) => {
      setUploadStatus("error");
      setError(error.message);
      toast({
        title: "Resume processing failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleGetUploadParameters = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      return {
        method: "PUT" as const,
        url: data.uploadURL,
      };
    } catch (error) {
      throw new Error("Failed to get upload URL");
    }
  };

  const handleFileUpload = async (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      setUploadStatus("processing");
      setUploadProgress(100);

      // Create a file object from the uploaded data
      const file = uploadedFile.data as File;
      processResumeMutation.mutate(file);
    }
  };

  const getStatusIcon = () => {
    switch (uploadStatus) {
      case "uploading":
        return <Loader2 className="w-5 h-5 animate-spin text-blue-600" />;
      case "processing":
        return <Brain className="w-5 h-5 text-purple-600" />;
      case "complete":
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case "error":
        return <AlertCircle className="w-5 h-5 text-red-600" />;
      default:
        return <FileText className="w-5 h-5 text-muted-foreground" />;
    }
  };

  const getStatusText = () => {
    switch (uploadStatus) {
      case "uploading":
        return "Uploading resume...";
      case "processing":
        return "Processing with AI...";
      case "complete":
        return "Analysis complete";
      case "error":
        return "Upload failed";
      default:
        return "Ready to upload";
    }
  };

  if (analysisResult) {
    return (
      <div className="space-y-6">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle className="flex items-center space-x-2">
                <CheckCircle className="w-5 h-5 text-green-600" />
                <span>Resume Analysis Complete</span>
              </CardTitle>
              <Button
                variant="outline"
                size="sm"
                onClick={() => {
                  setAnalysisResult(null);
                  setUploadStatus("idle");
                  setError(null);
                }}
                data-testid="button-upload-another"
              >
                Upload Another Resume
              </Button>
            </div>
          </CardHeader>
        </Card>

        <ResumeAnalysis analysis={analysisResult} />
      </div>
    );
  }

  return (
    <Card data-testid="card-resume-uploader">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <FileText className="w-5 h-5" />
          <span>Resume Upload & Analysis</span>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        {/* Upload Status */}
        <div className="flex items-center space-x-3">
          {getStatusIcon()}
          <div className="flex-1">
            <p className="text-sm font-medium" data-testid="text-upload-status">
              {getStatusText()}
            </p>
            {(uploadStatus === "uploading" || uploadStatus === "processing") && (
              <Progress value={uploadProgress} className="mt-2" />
            )}
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="p-4 bg-destructive/10 border border-destructive/20 rounded-lg">
            <div className="flex items-center space-x-2">
              <AlertCircle className="w-4 h-4 text-destructive" />
              <p className="text-sm text-destructive" data-testid="text-error">
                {error}
              </p>
            </div>
          </div>
        )}

        {/* Upload Instructions */}
        {uploadStatus === "idle" && (
          <div className="text-center space-y-4">
            <div className="p-8 border-2 border-dashed border-border rounded-lg">
              <Upload className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium text-foreground mb-2">
                Upload Resume for AI Analysis
              </h3>
              <p className="text-sm text-muted-foreground mb-4">
                Support for PDF and text files. Maximum file size: 10MB
              </p>

              <ObjectUploader
                maxNumberOfFiles={1}
                maxFileSize={10 * 1024 * 1024} // 10MB
                onGetUploadParameters={handleGetUploadParameters}
                onComplete={handleFileUpload}
                buttonClassName="bg-primary text-primary-foreground hover:bg-primary/90"
                data-testid="button-upload-resume"
              >
                <Upload className="w-4 h-4 mr-2" />
                Choose Resume File
              </ObjectUploader>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div className="text-center">
                <Brain className="w-8 h-8 text-primary mx-auto mb-2" />
                <h4 className="font-medium">AI Analysis</h4>
                <p className="text-muted-foreground">
                  Extract skills, experience, and insights
                </p>
              </div>
              <div className="text-center">
                <CheckCircle className="w-8 h-8 text-green-600 mx-auto mb-2" />
                <h4 className="font-medium">Auto-Population</h4>
                <p className="text-muted-foreground">
                  Automatically fill candidate profile
                </p>
              </div>
              <div className="text-center">
                <FileText className="w-8 h-8 text-blue-600 mx-auto mb-2" />
                <h4 className="font-medium">Smart Matching</h4>
                <p className="text-muted-foreground">
                  Match to relevant job positions
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Processing Status */}
        {(uploadStatus === "uploading" || uploadStatus === "processing") && (
          <div className="text-center space-y-4">
            <div className="p-8 bg-accent rounded-lg">
              {uploadStatus === "uploading" ? (
                <>
                  <Upload className="w-12 h-12 text-blue-600 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-foreground mb-2">
                    Uploading Resume
                  </h3>
                  <p className="text-sm text-muted-foreground">
                    Secure upload in progress...
                  </p>
                </>
              ) : (
                <>
                  <Brain className="w-12 h-12 text-purple-600 mx-auto mb-4 animate-pulse" />
                  <h3 className="text-lg font-medium text-foreground mb-2">
                    AI Analysis in Progress
                  </h3>
                  <p className="text-sm text-muted-foreground">
                    Extracting skills, experience, and generating insights...
                  </p>
                </>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-candidates.ts.content:
import { useQuery } from "@tanstack/react-query";
import { type Candidate } from "@shared/schema";

export function useCandidates(searchQuery?: string) {
  return useQuery<Candidate[]>({
    queryKey: ["/api/candidates", { search: searchQuery }],
    queryFn: async ({ queryKey }) => {
      const [url, params] = queryKey as [string, { search?: string }];
      const searchParams = new URLSearchParams();

      if (params.search) {
        searchParams.append("search", params.search);
      }

      const fullUrl = searchParams.toString() ? `${url}?${searchParams}` : url;
      const response = await fetch(fullUrl, { credentials: "include" });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
  });
}

export function useCandidate(id: string) {
  return useQuery<Candidate>({
    queryKey: ["/api/candidates", id],
    enabled: !!id,
  });
}

