import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useWebSocketContext } from "@/contexts/websocket-context";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { 
  Bell, 
  BellRing,
  X,
  Check,
  AlertCircle,
  Info,
  CheckCircle
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface Notification {
  id: string;
  userId: string;
  type: string;
  title: string;
  message: string;
  entityType?: string;
  entityId?: string;
  isRead: boolean;
  createdAt: string;
}

const notificationIcons: Record<string, any> = {
  'team_activity': Info,
  'candidate_update': AlertCircle,
  'interview_reminder': BellRing,
  'system': CheckCircle,
};

const notificationColors: Record<string, string> = {
  'team_activity': 'text-blue-500',
  'candidate_update': 'text-orange-500',
  'interview_reminder': 'text-purple-500',
  'system': 'text-green-500',
};

export function NotificationPanel() {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const queryClient = useQueryClient();

  // Mock user ID - in real app this would come from auth context
  const userId = "hr-user-1";

  const { data: notificationData } = useQuery<Notification[]>({
    queryKey: ["/api/notifications", userId],
    queryFn: async () => {
      const response = await fetch(`/api/notifications?userId=${userId}`, {
        credentials: "include",
      });
      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }
      return response.json();
    },
  });

  const markAsReadMutation = useMutation({
    mutationFn: async (notificationId: string) => {
      return apiRequest("PATCH", `/api/notifications/${notificationId}/read`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications", userId] });
    },
  });

  const { isConnected, subscribe } = useWebSocketContext();

  // Handle real-time notifications
  useEffect(() => {
    const unsubscribe = subscribe((message: any) => {
      if (message.type === 'notification') {
        const newNotification = message.payload;
        setNotifications(prev => [newNotification, ...prev]);

        // Invalidate notifications query to refresh
        queryClient.invalidateQueries({ queryKey: ["/api/notifications", userId] });
      }
    });

    return unsubscribe;
  }, [subscribe, queryClient, userId]);

  useEffect(() => {
    if (notificationData) {
      setNotifications(notificationData);
    }
  }, [notificationData]);

  const unreadCount = notifications.filter(n => !n.isRead).length;

  const handleMarkAsRead = (notificationId: string) => {
    markAsReadMutation.mutate(notificationId);

    // Optimistically update local state
    setNotifications(prev => 
      prev.map(n => 
        n.id === notificationId ? { ...n, isRead: true } : n
      )
    );
  };

  const handleMarkAllAsRead = () => {
    const unreadNotifications = notifications.filter(n => !n.isRead);
    unreadNotifications.forEach(notification => {
      markAsReadMutation.mutate(notification.id);
    });

    // Optimistically update local state
    setNotifications(prev => 
      prev.map(n => ({ ...n, isRead: true }))
    );
  };

  return (
    <div className="relative">
      <Button
        variant="ghost"
        size="sm"
        className="relative"
        onClick={() => setIsOpen(!isOpen)}
        data-testid="button-notifications"
      >
        <Bell className="w-4 h-4" />
        {unreadCount > 0 && (
          <Badge 
            variant="destructive" 
            className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs"
          >
            {unreadCount > 99 ? '99+' : unreadCount}
          </Badge>
        )}
      </Button>

      {isOpen && (
        <Card className="absolute right-0 top-full mt-2 w-80 z-50 shadow-lg border">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Notifications</CardTitle>
            <div className="flex items-center space-x-2">
              {unreadCount > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleMarkAllAsRead}
                  className="text-xs"
                >
                  Mark all read
                </Button>
              )}
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsOpen(false)}
              >
                <X className="w-4 h-4" />
              </Button>
            </div>
          </CardHeader>
          <CardContent className="p-0">
            <ScrollArea className="h-80">
              {notifications.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <Bell className="w-8 h-8 mx-auto mb-2 opacity-50" />
                  <p className="text-sm">No notifications</p>
                </div>
              ) : (
                <div className="space-y-1">
                  {notifications.map((notification) => {
                    const IconComponent = notificationIcons[notification.type] || Info;
                    const iconColor = notificationColors[notification.type] || 'text-gray-500';

                    return (
                      <div 
                        key={notification.id} 
                        className={`p-3 border-b border-border hover:bg-accent/50 transition-colors ${
                          !notification.isRead ? 'bg-accent/30' : ''
                        }`}
                      >
                        <div className="flex items-start space-x-3">
                          <IconComponent className={`w-4 h-4 mt-1 ${iconColor}`} />
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between">
                              <p className="text-sm font-medium text-foreground">
                                {notification.title}
                              </p>
                              {!notification.isRead && (
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => handleMarkAsRead(notification.id)}
                                  className="h-6 w-6 p-0"
                                >
                                  <Check className="w-3 h-3" />
                                </Button>
                              )}
                            </div>
                            <p className="text-xs text-muted-foreground mt-1">
                              {notification.message}
                            </p>
                            <div className="flex items-center justify-between mt-2">
                              <Badge variant="outline" className="text-xs">
                                {notification.type.replace('_', ' ')}
                              </Badge>
                              <span className="text-xs text-muted-foreground">
                                {formatDistanceToNow(new Date(notification.createdAt), { addSuffix: true })}
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </ScrollArea>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/online-users.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { useWebSocketContext } from "@/contexts/websocket-context";
import { useQuery } from "@tanstack/react-query";
import { Users, Circle } from "lucide-react";

interface User {
  id: string;
  name: string;
  email: string;
  role: string;
}

export function OnlineUsers() {
  const [onlineUsers, setOnlineUsers] = useState<User[]>([]);

  const { data: onlineData } = useQuery<User[]>({
    queryKey: ["/api/team/online"],
  });

  const { isConnected, subscribe } = useWebSocketContext();

  // Handle real-time user status updates
  useEffect(() => {
    const unsubscribe = subscribe((message: any) => {
      if (message.type === 'user_online') {
        // Refetch online users when someone comes online
        // In a real app, we could add the user directly to avoid refetch
      } else if (message.type === 'user_offline') {
        // Remove user from online list
        setOnlineUsers(prev => prev.filter(user => user.id !== message.payload.userId));
      } else if (message.type === 'team_status') {
        // Update full team status
        setOnlineUsers(message.payload.onlineUsers || []);
      }
    });

    return unsubscribe;
  }, [subscribe]);

  useEffect(() => {
    if (onlineData) {
      setOnlineUsers(onlineData);
    }
  }, [onlineData]);

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'hr_manager':
        return 'bg-blue-500';
      case 'recruiter':
        return 'bg-green-500';
      case 'admin':
        return 'bg-purple-500';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <Card className="h-full">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="text-sm font-medium">Team Online</CardTitle>
        <div className="flex items-center space-x-2">
          <Circle className="w-2 h-2 fill-green-500 text-green-500" />
          <span className="text-xs text-muted-foreground">
            {onlineUsers.length} online
          </span>
        </div>
      </CardHeader>
      <CardContent>
        {onlineUsers.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <Users className="w-8 h-8 mx-auto mb-2 opacity-50" />
            <p className="text-sm">No one is online</p>
          </div>
        ) : (
          <div className="space-y-3">
            {onlineUsers.map((user) => (
              <div key={user.id} className="flex items-center space-x-3">
                <div className="relative">
                  <Avatar className="w-8 h-8">
                    <AvatarFallback className={`text-xs font-medium text-white ${getRoleColor(user.role)}`}>
                      {getInitials(user.name)}
                    </AvatarFallback>
                  </Avatar>
                  <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-green-500 border-2 border-background rounded-full" />
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm font-medium text-foreground truncate">
                    {user.name}
                  </p>
                  <div className="flex items-center space-x-2">
                    <Badge variant="secondary" className="text-xs">
                      {user.role.replace('_', ' ')}
                    </Badge>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/team-activity.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useWebSocketContext } from "@/contexts/websocket-context";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { 
  Users, 
  UserPlus, 
  Calendar, 
  Briefcase, 
  FileText,
  Clock,
  Eye
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";

interface ActivityLog {
  id: string;
  userId: string;
  action: string;
  entityType: string;
  entityId: string;
  entityName: string;
  details?: any;
  createdAt: string;
  userName?: string;
}

const actionIcons: Record<string, any> = {
  'candidate_created': UserPlus,
  'candidate_updated': Users,
  'job_created': Briefcase,
  'job_updated': Briefcase,
  'interview_scheduled': Calendar,
  'interview_updated': Calendar,
  'comment_added': FileText,
  'resume_viewed': Eye,
};

const actionColors: Record<string, string> = {
  'candidate_created': 'bg-green-500',
  'candidate_updated': 'bg-blue-500', 
  'job_created': 'bg-purple-500',
  'job_updated': 'bg-purple-400',
  'interview_scheduled': 'bg-orange-500',
  'interview_updated': 'bg-orange-400',
  'comment_added': 'bg-gray-500',
  'resume_viewed': 'bg-indigo-500',
};

export function TeamActivity() {
  const [activities, setActivities] = useState<ActivityLog[]>([]);
  const queryClient = useQueryClient();

  const { data: activityData } = useQuery<ActivityLog[]>({
    queryKey: ["/api/activity"],
  });

  const { isConnected, subscribe } = useWebSocketContext();

  // Handle real-time activity updates
  useEffect(() => {
    const unsubscribe = subscribe((message: any) => {
      if (message.type === 'team_activity') {
        const newActivity = message.payload;
        setActivities(prev => [newActivity, ...prev.slice(0, 49)]); // Keep last 50 activities

        // Invalidate activity query to refresh
        queryClient.invalidateQueries({ queryKey: ["/api/activity"] });
      }
    });

    return unsubscribe;
  }, [subscribe, queryClient]);

  useEffect(() => {
    if (activityData) {
      setActivities(activityData.slice(0, 50));
    }
  }, [activityData]);

  const getActivityDescription = (activity: ActivityLog) => {
    const userName = activity.userName || 'Someone';

    switch (activity.action) {
      case 'candidate_created':
        return `${userName} added new candidate ${activity.entityName}`;
      case 'candidate_updated':
        return `${userName} updated candidate ${activity.entityName}`;
      case 'job_created':
        return `${userName} posted new job ${activity.entityName}`;
      case 'job_updated':
        return `${userName} updated job ${activity.entityName}`;
      case 'interview_scheduled':
        return `${userName} scheduled interview with ${activity.entityName}`;
      case 'interview_updated':
        return `${userName} updated interview with ${activity.entityName}`;
      case 'comment_added':
        return `${userName} commented on ${activity.entityName}`;
      case 'resume_viewed':
        return `${userName} viewed resume for ${activity.entityName}`;
      default:
        return `${userName} performed ${activity.action} on ${activity.entityName}`;
    }
  };

  return (
    <Card className="h-full">
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="text-sm font-medium">Team Activity</CardTitle>
        <div className="flex items-center space-x-2">
          <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`} />
          <span className="text-xs text-muted-foreground">
            {isConnected ? 'Live' : 'Offline'}
          </span>
        </div>
      </CardHeader>
      <CardContent>
        <ScrollArea className="h-96">
          {activities.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <Clock className="w-8 h-8 mx-auto mb-2 opacity-50" />
              <p>No recent team activity</p>
            </div>
          ) : (
            <div className="space-y-3">
              {activities.map((activity) => {
                const IconComponent = actionIcons[activity.action] || FileText;
                const iconColor = actionColors[activity.action] || 'bg-gray-500';

                return (
                  <div key={activity.id} className="flex items-start space-x-3 p-2 rounded-lg hover:bg-accent/50 transition-colors">
                    <div className={`w-8 h-8 rounded-full ${iconColor} flex items-center justify-center flex-shrink-0`}>
                      <IconComponent className="w-4 h-4 text-white" />
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="text-sm font-medium text-foreground">
                        {getActivityDescription(activity)}
                      </p>
                      <div className="flex items-center justify-between mt-1">
                        <Badge variant="secondary" className="text-xs">
                          {activity.entityType}
                        </Badge>
                        <span className="text-xs text-muted-foreground">
                          {formatDistanceToNow(new Date(activity.createdAt), { addSuffix: true })}
                        </span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </ScrollArea>
      </CardContent>
    </Card>
  );
}

client/src/contexts/websocket-context.ts.content:
import { createContext, useContext, useEffect, useRef, useState, useCallback, ReactNode } from 'react';
import { useToast } from '@/hooks/use-toast';
import { useLocation } from 'wouter';

interface WSMessage {
  type: string;
  payload?: any;
}

interface WebSocketContextType {
  isConnected: boolean;
  sendMessage: (message: WSMessage) => void;
  subscribe: (callback: (message: WSMessage) => void) => () => void;
  lastMessage: WSMessage | null;
}

const WebSocketContext = createContext<WebSocketContextType | null>(null);

interface WebSocketProviderProps {
  children: ReactNode;
  autoConnect?: boolean;
}

export function WebSocketProvider({ children, autoConnect = true }: WebSocketProviderProps) {
  const [isConnected, setIsConnected] = useState(false);
  const [lastMessage, setLastMessage] = useState<WSMessage | null>(null);
  const ws = useRef<WebSocket | null>(null);
  const subscribers = useRef<Set<(message: WSMessage) => void>>(new Set());
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 10;
  const { toast } = useToast();
  const [location] = useLocation();

  // Get the correct WebSocket URL based on current protocol
  const getWebSocketURL = useCallback(() => {
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const host = window.location.host;
    return `${protocol}//${host}`;
  }, []);

  // Calculate reconnection delay with exponential backoff and jitter
  const getReconnectDelay = useCallback((attempt: number) => {
    const baseDelay = Math.min(1000 * Math.pow(2, attempt), 30000); // Max 30 seconds
    const jitter = Math.random() * 1000; // Add up to 1 second of jitter
    return baseDelay + jitter;
  }, []);

  const connect = useCallback(() => {
    if (ws.current?.readyState === WebSocket.OPEN) {
      return; // Already connected
    }

    try {
      const wsUrl = getWebSocketURL();
      ws.current = new WebSocket(wsUrl);

      ws.current.onopen = () => {
        setIsConnected(true);
        reconnectAttempts.current = 0;

        // Authenticate with user ID (mock for now)
        const userId = "hr-user-1"; // In real app, this would come from auth context
        ws.current?.send(JSON.stringify({
          type: 'authenticate',
          payload: { userId }
        }));

        // Send current page
        ws.current?.send(JSON.stringify({
          type: 'page_change',
          payload: { page: location }
        }));
      };

      ws.current.onmessage = (event) => {
        try {
          const message: WSMessage = JSON.parse(event.data);
          setLastMessage(message);

          // Notify all subscribers
          subscribers.current.forEach(callback => {
            try {
              callback(message);
            } catch (error) {
              console.error('Error in WebSocket message subscriber:', error);
            }
          });

          // Handle global message types
          switch (message.type) {
            case 'notification':
              toast({
                title: message.payload.title,
                description: message.payload.message,
              });
              break;
            case 'pong':
              // Heartbeat response - connection is alive
              break;
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      ws.current.onclose = () => {
        setIsConnected(false);

        // Attempt to reconnect if auto-connect is enabled
        if (autoConnect && reconnectAttempts.current < maxReconnectAttempts) {
          const delay = getReconnectDelay(reconnectAttempts.current);
          reconnectAttempts.current++;

          setTimeout(() => {
            connect();
          }, delay);
        }
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };

    } catch (error) {
      console.error('Failed to create WebSocket connection:', error);
      setIsConnected(false);
    }
  }, [getWebSocketURL, autoConnect, toast, location, getReconnectDelay]);

  const disconnect = useCallback(() => {
    if (ws.current) {
      ws.current.close();
      ws.current = null;
    }
  }, []);

  const sendMessage = useCallback((message: WSMessage) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify(message));
    } else {
      console.warn('WebSocket is not connected');
    }
  }, []);

  const subscribe = useCallback((callback: (message: WSMessage) => void) => {
    subscribers.current.add(callback);

    // Return unsubscribe function
    return () => {
      subscribers.current.delete(callback);
    };
  }, []);

  // Handle navigation changes
  useEffect(() => {
    if (isConnected) {
      sendMessage({
        type: 'page_change',
        payload: { page: location }
      });
    }
  }, [location, isConnected, sendMessage]);

  // Connect on mount
  useEffect(() => {
    if (autoConnect) {
      connect();
    }

    return () => {
      disconnect();
    };
  }, [autoConnect, connect, disconnect]);

  // Heartbeat mechanism
  useEffect(() => {
    if (!isConnected) return;

    const heartbeatInterval = setInterval(() => {
      sendMessage({ type: 'ping' });
    }, 30000); // Send ping every 30 seconds

    return () => {
      clearInterval(heartbeatInterval);
    };
  }, [isConnected, sendMessage]);

  const contextValue: WebSocketContextType = {
    isConnected,
    sendMessage,
    subscribe,
    lastMessage,
  };

  return (
    <WebSocketContext.Provider value={contextValue}>
      {children}
    </WebSocketContext.Provider>
  );
}

export function useWebSocketContext() {
  const context = useContext(WebSocketContext);
  if (!context) {
    throw new Error('useWebSocketContext must be used within a WebSocketProvider');
  }
  return context;
}
